                        isis_maximum_paths: int | None = None,
                        is_type: Annotated[str, ValidValues["level-1-2", "level-1", "level-2"]] | None = "level-2",
                        node_sid_base: int | None = 0,
                        loopback_ipv4_pool: Annotated[str, Format["ipv4_cidr"]] | None = None,
