                        mlag_peer_vlan: Annotated[int, Min[1], Max[4094]] | None = 4094,
                        mlag_peer_link_allowed_vlans: str | None = None,
                        mlag_peer_address_family: Annotated[str, ValidValues["ipv4", "ipv6"]] | None = "ipv4",
                        mlag_peer_ipv4_pool: Annotated[str, Format["ipv4_cidr"]] | None = None,
                        mlag_peer_ipv6_pool: Annotated[str, Format["ipv6_cidr"]] | None = None,
                        mlag_port_channel_id: int | None = None,
