                        inband_mgmt_gateway: Annotated[str, Format["ipv4"]] | None = None,
                        inband_mgmt_ipv6_address: Annotated[str, Format["ipv6"]] | None = None,
                        inband_mgmt_ipv6_subnet: Annotated[str, Format["ipv6_cidr"]] | None = None,
                        inband_mgmt_ipv6_gateway: Annotated[str, Format["ipv6"]] | None = None,
                        inband_mgmt_description: str | None = "Inband Management",
                        inband_mgmt_vlan_name: str | None = "Inband Management",
